import React, { useState } from "react";
import "./Chessboard.css";

function Chessboard() {
  const board_array = [];
  for (var i = 0; i <= 63; i++) {
    console.log("i", i);
    if (i % 8 == i % 2 && i % 8 == 0) {
      board_array.push(<div className="black" />);
    } else if (i % 2) {
      board_array.push(<div className="white" />);
    } else {
      board_array.push(<div className="white" />);
    }
  }

  return (
    <div className="Chessboard">
      <div id="chessboard">
        {board_array}
        <div class="bottom-notation">a</div>
        <div class="bottom-notation">b</div>
        <div class="bottom-notation">c</div>
        <div class="bottom-notation">d</div>
        <div class="bottom-notation">e</div>
        <div class="bottom-notation">f</div>
        <div class="bottom-notation">g</div>
        <div class="bottom-notation">h</div>
      </div>
    </div>
  );
}

export default Chessboard;
