import React, { useState } from "react";
import "./Chessboard.css";

function Chessboard() {
  function isOdd(num) {
    return num % 2;
  }
  function alternateColor(color) {
    var color;
    if (color == "black") {
      color = "white";
    } else {
      color = "black";
    }
    return color;
  }
  const board_array = [];
  for (var i = 0; i <= 63; i++) {
    console.log("i", i);
    var color = "white";
    if (i % 2) {
      board_array.push(<div className="`${color}`" />);
    } else {
      board_array.push(<div className="`${color}`" />);
    }
  }

  return (
    <div className="Chessboard">
      <div id="chessboard">
        {board_array}
        <div className="bottom-notation">a</div>
        <div className="bottom-notation">b</div>
        <div className="bottom-notation">c</div>
        <div className="bottom-notation">d</div>
        <div className="bottom-notation">e</div>
        <div className="bottom-notation">f</div>
        <div className="bottom-notation">g</div>
        <div className="bottom-notation">h</div>
      </div>
    </div>
  );
}

export default Chessboard;
